const subFunc = {
  getSub: function(l1, l2, k1, k2) {
    let l3 = []
    if (k1 !== undefined && k2 !== undefined) {
      $.each(l1, (index, item) => {
        let flag = true
        $.each(l2, (i, t) => {
          if (t[k2] === item[k1]) {
            flag = false
          }
        })
        if (flag) {
          l3.push(item)
        }
      })
    }
    if (k1 !== undefined && k2 === undefined) {
      $.each(l1, (index, item) => {
        let flag = true
        $.each(l2, (i, t) => {
          if (t === item[k1]) {
            flag = false
          }
        })
        if (flag) {
          l3.push(item)
        }
      })
    }
    if (k1 === undefined && k2 === undefined) {
      $.each(l1, (index, item) => {
        let flag = true
        $.each(l2, (i, t) => {
          if (t === item) {
            flag = false
          }
        })
        if (flag) {
          l3.push(item)
        }
      })
    }
    return l3
  },
  arrangeSelected(list, key) {
    let arr = []
    $.each(list, (index, item) => {
      arr.push(item[key])
    })
    return arr
  }
}

module.exports = subFunc