const date = {
  renderDateString(d, bool) {
    const y = new Date(d).getFullYear()
    return y === 0001 || y === 1900 ? '-' : ((bool ? d.replace('T', ' ') : d.split('T')[0]))
  },
  initDatePicker(dates) {
    dates.datetimepicker({
      language: "zh-CN",
      weekStart: 1,
      todayBtn: 1,
      autoclose: 1,
      todayHighlight: 1,
      startView: 2,
      minView: 2,
      forceParse: 0,
      format: "yyyy-mm-dd"
    });
  },
  initDatetimePicker(dates) {
    dates.datetimepicker({
      weekStart: 1,
      todayBtn: 1,
      autoclose: 1,
      todayHighlight: 1,
      startView: 2,
      forceParse: 0,
      showMeridian: 1,
      format: "yyyy-mm-dd hh:ii"
    });
  },
  initTimePicker(dates) {
    dates.datetimepicker({
      language: "zh-CN",
      weekStart: 1,
      todayBtn: 1,
      autoclose: 1,
      todayHighlight: 1,
      startView: 1,
      minView: 0,
      maxView: 1,
      forceParse: 0,
      format: 'hh:ii'
    });
  }
}

module.exports = date