const form = {
  isEnterSearchKey: function(event, cb) {
    const ev = event || window.event
    if (ev.keyCode === 13) {
      cb()
      ev.preventDefault()
    }
  },

  showFormError: function(obj) {
    $(obj).parent().addClass('has-error')
  },

  // 去掉输入框的表单验证
  removeFormError: function(obj, isForce) {
    if (obj.value !== "" || isForce) {
      $(obj).parent().removeClass('has-error')
    }
  }
}

module.exports = form