const formatter = {
  isMobile: function(p) {
    return /^0?1[3|4|5|6|7|8][0-9]\d{8}$/.test(p)
  },
  formatXlsFile: function(o) {
    if (o.files.length < 1) {
      return
    }
    if (o.files[0].type !== 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet' && o.files[0].type !== 'application/vnd.ms-excel') {
      return false
    } else {
      return true
    }
  },
  isCompressFile: function(o) {
    if (o.files.length < 1) {
      return
    }
    const type = o.files[0].name.split('.').pop()
    if (type === 'rar' || type === 'zip') {
      return true
    } else {
      return false
    }
  },
  isEmail: function(p) {
    return /^[a-zA-Z0-9_-]+@[a-zA-Z0-9_-]+(\.[a-zA-Z0-9_-]+)+$/.test(p)
  },
  isInt: function(p) {
    return /^[1-9]\d*$/.test(p)
  }
}

module.exports = formatter