const lightbox = {
  renderListImage: function(imgurl, iw, ih) {
    const w = iw === undefined ? 45 : iw;
    const h = ih === undefined ? 45 : ih;
    return `<img class="pointer lightboxImg" style="width:${w}px;height:${h}px;" src="${imgurl}">`
  },
  initImageLightBox: function() {
    $('.lightboxImg').lightbox({
      image: this.src
    })
  }
}

module.exports = lightbox