const spin = {
  show: function() {
    const s = document.createElement('div')
    $(s).addClass('spin')
    $(s).html(`<i class="icon icon-spin icon-spinner-indicator"></i>`)
    document.body.appendChild(s)
  },
  hide: function() {
    document.body.removeChild($('.spin')[0])
  }
}

module.exports = spin